const cheerio = require\('cheerio'\);

async function test\(\) {
  const res = await fetch\('https://www.anthropic.com/news', {
    headers: { 'User-Agent': 'AI-Trends-Dashboard/1.0', 'Accept': 'text/html' }
  }\);
  const html = await res.text\(\);
  console.log\('HTML length:', html.length\);

  const $ = cheerio.load\(html\);
  const seen = new Set\(\);
  const articles: any[] = [];

  $\('a[href]'\).each\(\(_: any, el: any\) => {
    const href = $\(el\).attr\('href'\) || '';
    if \(!/^\(https:\\/\\/www\\.anthropic\\.com\)?\\/news\\/[a-z0-9]/.test\(href\)\) return;
    const url = href.startsWith\('/'\) ? 'https://www.anthropic.com' + href : href;
    if \(seen.has\(url\)\) return;
    seen.add\(url\);

    const $el = $\(el\);
    const $heading = $el.find\('h2, h3, h4, h5, h6'\).first\(\);
    let title = $heading.length ? $heading.text\(\).trim\(\) : '';
    if \(!title\) {
      const $titleSpan = $el.find\('[class*="title"]'\).first\(\);
      title = $titleSpan.length ? $titleSpan.text\(\).trim\(\) : '';
    }

    const textContent = $el.text\(\);
    const dateMatch = textContent.match\(/\(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec\)\\s+\\d{1,2},\\s+\\d{4}/\);

    articles.push\({
      url: url.substring\(0, 60\),
      title: title ? title.substring\(0, 60\) : '\(no title\)',
      date: dateMatch ? dateMatch[0] : '\(no date\)',
    }\);
  }\);

  console.log\('Total articles found:', articles.length\);
  console.log\('With titles:', articles.filter\(\(a: any\) => a.title !== '\(no title\)'\).length\);
  console.log\('With dates:', articles.filter\(\(a: any\) => a.date !== '\(no date\)'\).length\);
  console.log\('With both:', articles.filter\(\(a: any\) => a.title !== '\(no title\)' && a.date !== '\(no date\)'\).length\);
  console.log\('\\nFirst 5 articles:'\);
  articles.slice\(0, 5\).forEach\(\(a: any, i: number\) => console.log\(i + 1, a\)\);

  if \(articles.filter\(\(a: any\) => a.title !== '\(no title\)' && a.date !== '\(no date\)'\).length === 0\) {
    console.log\('\\n--- REGRESSION DETECTED ---'\);
    console.log\('No articles with both title AND date found.'\);
    console.log\('\\nAll links found:'\);
    articles.forEach\(\(a: any, i: number\) => console.log\(i + 1, a\)\);
  }
}

test\(\).catch\(console.error\);
